CREATE OR REPLACE PROCEDURE tasy_alterar_versao_tasy (cd_versao_p text, nm_usuario_p text) AS $body$
DECLARE


jobno	bigint;


BEGIN
	update aplicacao_tasy set cd_versao_atual = cd_versao_p, dt_atualizacao = clock_timestamp(), nm_usuario = nm_usuario_p
	where upper(cd_aplicacao_tasy) in ('TASY', 'TASYGER', 'TASYMED', 'TASYMON', 'TASYREL', 'TASYONC', 'TASYSUSUNI');
	commit;

	update	ajuste_versao_base_cliente
	set	ie_compila = 'S';
	commit;

	dbms_job.submit(jobno, 'tasy_criar_trigger_log_update;');
	commit;

end;
$body$
LANGUAGE PLPGSQL
