CREATE OR REPLACE PROCEDURE tasy_ativar_integridade (ie_validar_registro_p text, nm_tabela_p text default null) AS $body$
DECLARE


/*
ie_validar_registro_p
S - Valida os registros atuais na base
N - Não valida os registros atuais na base.

nm_tabela_p
Se for informada o nome da tabela irá ativar somente as integridades da mesma.
Se for informado null irá ativar todas as que estão inativas
*/
ds_comando_w 	varchar(255);
nm_tabela_w 	varchar(50);
nm_constraint_w varchar(50);
vl_retorno_w 	varchar(255);
ds_validar_w	varchar(20);

C01 CURSOR FOR
SELECT  TABLE_NAME,
		CONSTRAINT_NAME
FROM  	USER_CONSTRAINTS
WHERE  	STATUS = 'DISABLED'
AND		table_name = coalesce(upper(nm_tabela_p),table_name);


BEGIN

OPEN C01;
LOOP
FETCH C01 into
 nm_tabela_w,
 nm_constraint_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 begin

 if ( ie_validar_registro_p = 'N') then
	ds_validar_w := ' novalidate ';
 end if;

 ds_comando_w := 'ALTER TABLE ' || nm_tabela_w || ' enable '|| ds_validar_w ||' constraint ' || nm_constraint_w;
 CALL gravar_processo_longo(ds_comando_w,'TASY_ATIVAR_INTEGRIDADE',null);
 vl_retorno_w := Obter_Valor_Dinamico(ds_comando_w, vl_retorno_w);

 end;
END LOOP;
CLOSE C01;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
