CREATE OR REPLACE PROCEDURE tasy_ativar_trigger () AS $body$
DECLARE



ds_comando_w varchar(255);
nm_trigger_w varchar(30);
vl_retorno_w varchar(255);


C01 CURSOR FOR
SELECT  TRIGGER_NAME
FROM  	USER_TRIGGERS
WHERE  	STATUS = 'DISABLED';


BEGIN

OPEN C01;
LOOP
FETCH C01 into
 nm_trigger_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 begin

 ds_comando_w := 'ALTER TRIGGER ' || nm_trigger_w || ' enable ';

 vl_retorno_w := Obter_Valor_Dinamico(ds_comando_w, vl_retorno_w);

 end;
END LOOP;
CLOSE C01;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
