CREATE OR REPLACE PROCEDURE tasy_atualizar_motivo_desap () AS $body$
DECLARE


nr_seq_motivo_desap_w           integer;
nr_sequencia_w		        	bigint;

c02 CURSOR FOR
	SELECT distinct nr_seq_motivo_desap
	from laudo_paciente a
	where nr_seq_motivo_desap not in (SELECT nr_sequencia from laudo_paciente_mot_des b where a.nr_seq_motivo_desap = b.nr_sequencia);


BEGIN

	open	c02;
	loop
	fetch	c02 into
		nr_seq_motivo_desap_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */

	select nextval('laudo_paciente_mot_des_seq')
	into STRICT	nr_sequencia_w
	;

	insert into LAUDO_PACIENTE_MOT_DES(nr_sequencia, dt_atualizacao, nm_usuario, ds_motivo_desaprovacao) VALUES (nr_sequencia_w, clock_timestamp(), 'jrviel', Obter_Desc_Expressao(309569) ||' ' || nr_seq_motivo_desap_w);

	end	loop;
	close	c02;



end;
