CREATE OR REPLACE PROCEDURE tasy_bloquear_funcao_tab_temp () AS $body$
DECLARE


cd_funcao_w		integer;
c03			integer;
retorno_w		bigint;
-- Identifica funções que fazem uso de tabelas temporarias e bloqueia se houver alteração a ser realizada.
ds_cmd_c03_w	varchar(2000) :=
	' select	distinct a.cd_funcao  '||
	' from		tasy_versao.funcao_tabela a,'||
	' 		tasy_versao.tabela_sistema b, '||
	'		tasy_versao.tabela_atributo c'||
	' where		a.nm_tabela = b.nm_tabela  '||
	' and		c.nm_tabela = b.nm_tabela  '||
	' and		b.ie_temporaria = ''G'''||
	' and		c.dt_atualizacao > obter_data_geracao_versao';


BEGIN

C03 := DBMS_SQL.OPEN_CURSOR;
DBMS_SQL.PARSE(C03, ds_cmd_c03_w, dbms_sql.Native);
DBMS_SQL.DEFINE_COLUMN(C03, 1, cd_funcao_w);
retorno_w := DBMS_SQL.execute(c03);

while( DBMS_SQL.FETCH_ROWS(C03) > 0 ) loop

	DBMS_SQL.COLUMN_VALUE(C03, 1, cd_funcao_w);

	update	funcao
	set	ie_bloqueada = 'S'
	where	cd_funcao = cd_funcao_w;

END LOOP;

DBMS_SQL.CLOSE_CURSOR(C03);

commit;

end;
$body$
