SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE tasy_bloquear_usuario () AS $body$
DECLARE


ie_bloquear_w		bigint;
ie_bloquear_usu_w	bigint;
dt_ref_w		timestamp;
dt_acesso_w		timestamp;
nm_usuario_w		varchar(15);

c01 CURSOR FOR
SELECT	max(a.dt_acesso),
	a.nm_usuario
from	tasy_log_acesso a
where	exists (	select	1
		from	usuario x
		where	x.nm_usuario = a.nm_usuario
		and	x.ie_situacao = 'A')
group by	a.nm_usuario
order by	2;


BEGIN
select	max(coalesce(vl_parametro,vl_parametro_padrao))
into STRICT	ie_bloquear_w
from	funcao_parametro
where	cd_funcao	= 0
and	nr_sequencia	= 204;

open C01;
loop
fetch C01 into
	dt_acesso_w,
	nm_usuario_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	coalesce(max(a.vl_parametro),ie_bloquear_w)
	into STRICT	ie_bloquear_usu_w
	from	funcao_param_usuario a
	where	a.nm_usuario_param 	= nm_usuario_w
	and	a.cd_funcao 		= 0
	and	a.nr_sequencia		= 204;

	if (coalesce(ie_bloquear_usu_w,0) > 0) then
		begin
		dt_ref_w := clock_timestamp() - ie_bloquear_w;
		if (trunc(dt_acesso_w) < trunc(dt_ref_w)) then
			begin
			update	usuario
			set	ie_situacao = 'B'
			where	nm_usuario = nm_usuario_w;
			end;
		end if;
		end;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
