SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE tasy_consistir_ind_fk () AS $body$
DECLARE


ds_CONSTRAINT_TYPE_w		user_constraints.CONSTRAINT_TYPE%type;
ds_CONSTRAINT_NAME_w 		USER_CONSTRAINTS.CONSTRAINT_NAME%type;
ds_TABLE_NAME_w 			USER_CONSTRAINTS.TABLE_NAME%type;
ds_R_CONSTRAINT_NAME_w 		USER_CONSTRAINTS.R_CONSTRAINT_NAME%type;
ds_CONSTRAINT_NAME_COL_w 	USER_CONS_COLUMNS.CONSTRAINT_NAME%type;
ds_TABLE_NAME_COL_w 		USER_CONS_COLUMNS.TABLE_NAME%type;
ds_COLUMN_NAME_COL_w 		USER_CONS_COLUMNS.COLUMN_NAME%type;
ds_POSITION_COL_w 			USER_CONS_COLUMNS.POSITION%type;
ds_Dummy_w 					bigint;
ds_DummyChar_w 				varchar(2000);
ds_movito_w 				varchar(255);
ds_TABLE_NAME_USER_w		USER_CONSTRAINTS.TABLE_NAME%type;
ds_COLUMN_NAME_w 			USER_CONS_COLUMNS.COLUMN_NAME%type;
ds_POSITION_w 				USER_CONS_COLUMNS.POSITION%type;
nr_LineNum_w 				bigint;
nm_usuario_w				varchar(15);

c01 CURSOR FOR
	SELECT 	table_name
	from 	user_tables
	order 	by table_name;

c02 CURSOR FOR
	SELECT 	CONSTRAINT_TYPE,
		CONSTRAINT_NAME,
		R_CONSTRAINT_NAME
		from 	user_constraints
		where 	OWNER = USER
		and 	table_name = ds_TABLE_NAME_w
		and 	CONSTRAINT_TYPE = 'R'
		order 	by TABLE_NAME, CONSTRAINT_NAME;

c03 CURSOR FOR
	SELECT 	CONSTRAINT_NAME,
		TABLE_NAME,
		COLUMN_NAME,
		POSITION
	from 	user_cons_columns
	where 	OWNER = USER
	and 	CONSTRAINT_NAME = ds_CONSTRAINT_NAME_w
	order 	by POSITION;

c04 CURSOR FOR
	SELECT 	TABLE_NAME,
		COLUMN_NAME,
		POSITION
	from 	user_cons_columns
	where 	OWNER = USER
	and 	CONSTRAINT_NAME = ds_CONSTRAINT_NAME_w
	order 	by POSITION;


BEGIN
nr_LineNum_w	:= 0;
open	c01;
LOOP
	Fetch	c01 into
		ds_TABLE_NAME_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		if (nr_LineNum_w > 11) then
			exit;
		end	if;
		--constrants da tabela
		open	c02;
		LOOP
		FETCH 	c02 INTO
			ds_CONSTRAINT_TYPE_w,
			ds_CONSTRAINT_NAME_w,
			ds_R_CONSTRAINT_NAME_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */

			open 	c03;
			LOOP
			FETCH c03 INTO
				ds_CONSTRAINT_NAME_COL_w,
				ds_TABLE_NAME_COL_w,
				ds_COLUMN_NAME_COL_w,
				ds_POSITION_COL_w;
			EXIT WHEN NOT FOUND; /* apply on c03 */
				begin
					select	1
					into STRICT 	ds_POSITION_COL_w
					from 	user_ind_columns
					where 	TABLE_NAME = ds_TABLE_NAME_COL_w
					and 	COLUMN_NAME = ds_COLUMN_NAME_COL_w
					and 	COLUMN_POSITION = ds_POSITION_COL_w;
				exception
					--Somente para não deixar de tratar a exceção
					when 	too_many_rows then
						nr_LineNum_w	:= nr_LineNum_w + 1;
					when 	no_data_found then
						nr_LineNum_w	:= nr_LineNum_w + 1;

					select 	distinct TABLE_NAME
					into STRICT 	ds_DummyChar_w
					from 	user_cons_columns
					where 	OWNER = USER
					and 	CONSTRAINT_NAME = ds_R_CONSTRAINT_NAME_w;

					ds_movito_w	:= '';
					open	c04;
					loop
						Fetch c04 into
							ds_TABLE_NAME_USER_w,
							ds_COLUMN_NAME_w,
							ds_POSITION_w;
						EXIT WHEN NOT FOUND; /* apply on c04 */
							ds_movito_w	:= ds_COLUMN_NAME_w || ' ' || ds_POSITION_w || ' ';
					end	loop;
					close c04;
					nr_LineNum_w	:= nr_LineNum_w + 1;

					begin
						select	coalesce(NM_USUARIO, 'cetrentin')
						into STRICT 	nm_usuario_w
						from 	tabela_sistema
						where 	nm_tabela = ds_TABLE_NAME_COL_w;
					exception
						When	no_data_found then
							nm_usuario_w	:= null;
					end;

					if (nm_usuario_w IS NOT NULL AND nm_usuario_w::text <> '') then
						insert into w_inconsistencia_base(NM_TABELA, NM_OBJETO, DS_MOTIVO, DT_ATUALIZACAO, NM_USUARIO)
									values (ds_TABLE_NAME_COL_w, ds_DummyChar_w, obter_desc_expressao(284510)/*'Campos = '*/
||' = '||ds_movito_w||')', clock_timestamp(), nm_usuario_w);
						commit;
					end	if;
				end;
			end	loop;
			commit;
			close	c03;
		end	loop;
		close	c02;
end	loop;
close	c01;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE tasy_consistir_ind_fk () FROM PUBLIC;

