SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE tasy_criar_chave_primaria (nm_tabela_p text) AS $body$
DECLARE



qt_indice_w			bigint;
nm_indice_w			varchar(100);
nm_atributo_w      	varchar(50)    := '';
ds_virgula_w		varchar(01)	:= '';
ds_tablespace_ind_w varchar(50)    := '';
ds_comando_w		varchar(2000)	:= '';
qt_tamanho_w		integer;
vl_retorno_w		varchar(255);

C010 CURSOR FOR
	SELECT 	a.nm_atributo,
		coalesce(CASE WHEN ie_tipo_atributo='DATE' THEN 5  ELSE qt_tamanho END ,5)
	FROM 	tabela_atributo b,
		Indice_Atributo a
	WHERE b.nm_tabela		= nm_tabela_p
	  and a.nm_tabela		= nm_tabela_p
	  and a.nm_indice		= nm_indice_w
	  and a.nm_atributo	= b.nm_atributo
	order by a.nr_sequencia;


BEGIN

select	count(*)
into STRICT	qt_indice_w
from	indice a
where	a.nm_tabela = nm_tabela_p
and		a.ie_tipo = 'PK';

if (qt_indice_w = 1) then

	select	nm_indice
	into STRICT	nm_indice_w
	from	indice a
	where	a.nm_tabela	= nm_tabela_p
	and		a.ie_tipo = 'PK';

	ds_tablespace_ind_w := OBTER_VALOR_DINAMICO_CHAR_BV('select Obter_Tablespace_Index(:nm_Indice_p) from dual', 'nm_indice_p=' || nm_indice_w || ';', ds_tablespace_ind_w);
	ds_comando_w	:= 'alter table ' || nm_tabela_p || ' add (constraint ';
	ds_comando_w	:= ds_comando_w || nm_indice_w || ' Primary Key (';

	open C010;
	loop
	fetch C010 into
			nm_atributo_w,
			qt_tamanho_w;
	EXIT WHEN NOT FOUND; /* apply on C010 */
		begin
			ds_comando_w	:= ds_comando_w  || ds_virgula_w || nm_atributo_w;
			ds_virgula_w	:= ',';
		end;
	end loop;
	close C010;

	ds_comando_w	:= ds_comando_w  || ') Using Index Tablespace '|| ds_tablespace_ind_w|| ')';

	vl_retorno_w := Obter_Valor_Dinamico(ds_comando_w, vl_retorno_w);


end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE tasy_criar_chave_primaria (nm_tabela_p text) FROM PUBLIC;

