SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE tasy_criar_indices_pendente ( nm_indice_p text, IE_CRIA_INDICE_P text) AS $body$
DECLARE


job_w				bigint;
qt_job_w			bigint;
nm_indice_w			varchar(100);
ie_cria_indice_w	varchar(100);


C01 CURSOR FOR
	SELECT	job
	from	user_jobs
	where	what like '%TASY_CRIAR_INDICES_DINAMICO%';


BEGIN


/* Busca se existe Job que chama a Procedure */

select	count(*)
into STRICT	qt_job_w
from	user_jobs
where	what like '%TASY_CRIAR_INDICES_DINAMICO%';

if (qt_job_w > 0) then

	/* Caso houverem mais de 1 Job entao mata todas as antigas e deixa somente 1*/

	open C01;
	loop
	fetch C01 into
		job_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
			DBMS_JOB.remove(job_w);
			commit;
		end;
	end loop;
	close C01;
end if;

DBMS_JOB.SUBMIT(job_w
			   ,'TASY_CRIAR_INDICES_DINAMICO('||chr(39)||nm_indice_p||chr(39)||','||chr(39)||ie_cria_indice_p||chr(39)||');'
			   ,clock_timestamp()
			   ,'SYSDATE + 1/300');

commit;

DBMS_JOB.NEXT_DATE(job_w, clock_timestamp() + interval '1 days'/300);

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
