CREATE OR REPLACE PROCEDURE tasy_eliminar_relatorio (nm_usuario_p text) AS $body$
DECLARE


qt_relatorio_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_relatorio_w
from	relatorio;

if (qt_relatorio_w > 0) then
	delete	from relatorio_funcao a
	where not exists (SELECT 1 from relatorio b
		where a.nr_seq_relatorio	= b.nr_sequencia);
	delete	from relatorio_perfil a
	where not exists (SELECT 1 from relatorio b
		where a.nr_seq_relatorio	= b.nr_sequencia);
	delete	from Usuario_relatorio a
	where not exists (SELECT 1 from relatorio b
		where a.nr_seq_relatorio	= b.nr_sequencia);
	delete FROM relatorio_perfil_param a
	where not exists (SELECT 1 from relatorio_parametro b
		where a.nr_seq_parametro  = b.nr_sequencia);
	commit;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
