CREATE OR REPLACE PROCEDURE tasy_inserir_usuario_data ( nm_usuario_p text) AS $body$
DECLARE


nm_tabela_w			varchar(50);

C010 CURSOR FOR
SELECT nm_tabela
from Tabela_Sistema A
where qt_registros_previsto < 120
and not exists (select 1 from tabela_atributo b
	where a.nm_tabela = b.nm_tabela
	and b.nm_atributo = 'NM_USUARIO_NREC');


BEGIN

OPEN C010;
LOOP
FETCH C010 INTO 	nm_tabela_w;
EXIT WHEN NOT FOUND; /* apply on C010 */
	CALL Insere_Atributo_NRec(nm_tabela_w, nm_usuario_p);
END LOOP;
CLOSE C010;

END;
