CREATE OR REPLACE PROCEDURE tasy_liberar_tabela ( nm_tabela_p text) AS $body$
DECLARE


ds_comando_w	 varchar(500);

C01 CURSOR FOR
	SELECT	sid || ',' || serial# || ',' || bd
	from	processo_lock_v
	where	upper(nm_objeto) = upper(nm_tabela_p)
	and	sid not in (	SELECT	sid
				from	processo_longo_v);


BEGIN

open C01;
loop
fetch C01 into
	ds_comando_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	--execute immediate ds_comando_w;
	CALL exec_sql_dinamico_bv('', 'begin TASY_ELIMINAR_PROCESSO_LOCK(' || ds_comando_w || '); end;', '');
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
