CREATE OR REPLACE PROCEDURE tasy_marcar_alerta_ciente (nr_seq_ocorrencia_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (coalesce(nr_seq_ocorrencia_p,0) = 0) then
	update	alerta_tasy_ocor
	set	nm_usuario_ciente	= nm_usuario_p,
		dt_usuario_ciente	= clock_timestamp(),
		ie_usuario_ciente	= 'S'
	where	nm_usuario_alertado	= nm_usuario_p
	and	ie_usuario_ciente	= 'N';
else
	update	alerta_tasy_ocor
	set	nm_usuario_ciente	= nm_usuario_p,
		dt_usuario_ciente	= clock_timestamp(),
		ie_usuario_ciente	= 'S'
	where	nr_sequencia		= nr_seq_ocorrencia_p;
end if;
commit;

end;
