CREATE OR REPLACE PROCEDURE tasy_obter_cor ( nr_seq_cor_p bigint, cd_perfil_p bigint, cd_estabelecimento_p bigint, ds_cor_fundo_p INOUT text, ds_cor_fonte_p INOUT text, ds_cor_selecao_p INOUT text) AS $body$
DECLARE


ds_cor_fundo_w	varchar(15);
ds_cor_fonte_w	varchar(15);
ds_cor_selecao_w	varchar(15);
ie_ignorar_cor_fundo_w	varchar(1) := null;
ie_ocultar_w	varchar(1) := 'N';


BEGIN

select	max(ds_cor_fundo),
	max(ds_cor_fonte),
	max(ds_cor_selecao),
	max(ie_ignorar_cor_fundo),
	max(ie_ocultar)
into STRICT	ds_cor_fundo_w,
	ds_cor_fonte_w,
	ds_cor_selecao_w,
	ie_ignorar_cor_fundo_w,
	ie_ocultar_w
from	tasy_padrao_cor_perfil
where	cd_estabelecimento	= cd_estabelecimento_p
and	nr_seq_padrao		= nr_seq_cor_p
and	cd_perfil		= cd_perfil_p;

if (ie_ocultar_w IS NOT NULL AND ie_ocultar_w::text <> '') and (ie_ocultar_w = 'S') then
	begin
	ds_cor_fundo_p		:= null;
	ds_cor_fonte_p		:= null;
	ds_cor_selecao_p	:= null;
	end;
else
	begin

	if (coalesce(ds_cor_fundo_w::text, '') = '') then
	select	max(ds_cor_fundo),
		max(ds_cor_fonte),
		max(ds_cor_selecao),
		max(ie_ocultar)
	into STRICT	ds_cor_fundo_w,
		ds_cor_fonte_w,
		ds_cor_selecao_w,
		ie_ocultar_w
	from	tasy_padrao_cor_perfil
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	nr_seq_padrao	= nr_seq_cor_p
	and	coalesce(cd_perfil::text, '') = '';
	end if;

	if (coalesce(ds_cor_fundo_w::text, '') = '') then
		select	max(ds_cor_fundo),
			max(ds_cor_selecao)
		into STRICT	ds_cor_fundo_w,
			ds_cor_selecao_w
		from	tasy_padrao_cor
		where	nr_sequencia		= nr_seq_cor_p;
	end	if;

	if (coalesce(ds_cor_fonte_w::text, '') = '') then
		select	max(ds_cor_fonte)
		into STRICT	ds_cor_fonte_w
		from	tasy_padrao_cor
		where	nr_sequencia		= nr_seq_cor_p;
	end	if;

	if (ie_ignorar_cor_fundo_w = 'S') then
		ds_cor_fundo_p	:= null;
	else

		ds_cor_fundo_p	:= ds_cor_fundo_w;
	end	if;

	ds_cor_fonte_p		:= ds_cor_fonte_w;
	ds_cor_selecao_p	:= ds_cor_selecao_w;

	if (ie_ocultar_w = 'S') then
		ds_cor_fundo_p		:= null;
		ds_cor_fonte_p		:= null;
		ds_cor_selecao_p	:= null;
	end if;
	end;



end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
