CREATE OR REPLACE PROCEDURE tasy_trace ( ie_opcao_p bigint, ds_identificador_p text) AS $body$
DECLARE


ie_controle_w	integer;
ds_identificador_w	varchar(255);

BEGIN
if (ie_opcao_p = 0) then
	ds_identificador_w	:= substr(ds_identificador_p,1,36);
	select 1
	into STRICT ie_Controle_w
	;


	CALL exec_sql_dinamico_bv('','alter session set tracefile_identifier='||chr(39)||ds_identificador_w||chr(39),'');
	CALL exec_sql_dinamico_bv('','ALTER SESSION SET EVENTS ''10046 trace name context forever, level 12''','');

	select 1
	into STRICT ie_Controle_w
	;
else
	CALL exec_sql_dinamico_bv('','ALTER SESSION SET EVENTS ''10046 trace name context off''','');
	CALL exec_sql_dinamico_bv('','begin DBMS_SESSION.RESET_PACKAGE(); end;','');
end if;
end;
