CREATE OR REPLACE PROCEDURE tasy_versao_excluir_tabela ( nm_tabela_p text, ds_condicao_p text, nr_ordem_p bigint, ie_ativa_paralelism_p boolean default false) AS $body$
DECLARE


ds_comando_w	text;
qt_reg_orig_w	bigint;
nm_fase_w       cloud_upgrade_log.nm_fase_atualizacao%type := 'TASY_VERSAO_EXCLUIR_TABELA';
nm_paralelism_w varchar(1000) := '';


BEGIN

ds_comando_w	:= 'Select count(*) from '|| nm_tabela_p||' a ' ||ds_condicao_p;
qt_reg_orig_w := Obter_valor_Dinamico(ds_comando_w, qt_reg_orig_w);

if (qt_reg_orig_w > 0) then
    if (ie_ativa_paralelism_p) then
        nm_paralelism_w := '/*+PARALLEL('||nm_tabela_p||')*/';
    end if;
    ds_comando_w	:= 'delete'||trim(both nm_paralelism_w)||' from ' || nm_tabela_P || ' a ' ||ds_condicao_p;
    insert into TABELA_VERSAO_OFFLINE_W(nr_sequencia, nr_ordem, ds_comando, ds_log) values (nextval('tabela_versao_offline_w_seq'),nr_ordem_p,ds_comando_w,'E');
    commit;	
    CALL insert_log_cloud(null,null, 'atualizacao', nm_fase_w||'_LOG', null, nm_fase_w);
end if;

END;
