CREATE OR REPLACE PROCEDURE tax_add_change_situation ( sequence_p tax_certificate.nr_sequencia%type, stage_p bigint, value_p text ) AS $body$
DECLARE

field_w	varchar(255);
sql_w 	varchar(255);

BEGIN

	if (stage_p = 1) then
		field_w := 'IE_VALID_CERTIFICATE';
	elsif (stage_p = 2) then
		field_w := 'IE_CHAINS_GENERATED';
	elsif (stage_p = 3) then
		field_w := 'IE_COMMUNICATION_WSI';
	elsif (stage_p = 4) then
		field_w := 'IE_INSTALLED_CERTIFICATE';
	elsif (stage_p = 5) then
		field_w := 'IE_RECORDED_CERTIFICATE';
	end if;
	
	sql_w := 'update tax_certificate set ' || field_w || q'[ = ']' || value_p || q'[' where nr_sequencia = ]' || sequence_p;
	EXECUTE sql_w;
	commit;
	
end;
$body$
