CREATE OR REPLACE PROCEDURE terminar_alteracao_status_js ( ie_status_atend_p bigint, ds_sql_amostra_p text, ds_sql_motivo_alt_p text, nr_prescricao_p bigint, ie_status_atual_p bigint, ds_sql_param_p text, ie_restringir_p text, ie_opcao_p text, nr_seq_motivo_atraso_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_comando_update_w	varchar(4000);
ds_parametros_w		varchar(4000);
ds_sep_bv_w		varchar(50);


BEGIN

--ie_opcao_p = M->Material
--    	     E -> Exame
ds_sep_bv_w	:= obter_separador_bv;

if (ie_opcao_p = 'E') then

	ds_comando_update_w :=	' update	prescr_procedimento c ' ||
				' set 		ie_status_atend = :ie_status_atend ,' ||
				'    		nm_usuario 	= :nm_usuario ,' ||
				'     		dt_atualizacao 	= sysdate ' ||
				ds_sql_amostra_p ||
				ds_sql_motivo_alt_p ||
				' where 	nr_prescricao 	= :nr_prescricao ' ||
				' and 		ie_status_atend = :ie_status_atual ';


	if (ie_restringir_p = 'S') and (coalesce(ds_sql_param_p,'X') <> 'X') then
		ds_comando_update_w := ds_comando_update_w ||	' and 	c.cd_material_exame  in ('''||ds_sql_param_p||''') ';
	end if;

else

	ds_comando_update_w :=	' update	prescr_procedimento c ' ||
				' set 		ie_status_atend = :ie_status_atend ,' ||
				'    		nm_usuario 	= :nm_usuario ,' ||
				'     		dt_atualizacao 	= sysdate ' ||
				ds_sql_amostra_p ||
				ds_sql_motivo_alt_p ||
				' where 	nr_prescricao 	= :nr_prescricao ' ||
				' and 		ie_status_atend = :ie_status_atual ';



	if (ie_restringir_p = 'S') and (coalesce(ds_sql_param_p,'X') <> 'X') then

		ds_comando_update_w := ds_comando_update_w ||	' and 	nr_sequencia in ('||ds_sql_param_p||')';
	end if;

end if;

CALL exec_sql_dinamico_bv('', ds_comando_update_w, 	'ie_status_atend=' || to_char(ie_status_atend_p) || ds_sep_bv_w ||
						'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
						'nr_prescricao=' || to_char(nr_prescricao_p) || ds_sep_bv_w ||
						'ie_status_atual=' || to_char(ie_status_atual_p) || ds_sep_bv_w);
ds_comando_update_w := '';
if (ie_restringir_p = 'S') then

	if (ie_opcao_p = 'E') then
		ds_comando_update_w := ' and 	c.cd_material_exame  in ('''||ds_sql_param_p||''') ';
	else
		ds_comando_update_w := ' and 	c.nr_sequencia in ('||ds_sql_param_p||')';
	end if;

end if;

CALL lab_registra_motivo_atraso(nr_prescricao_p, nr_seq_motivo_atraso_p, ds_comando_update_w,nm_usuario_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
