CREATE OR REPLACE PROCEDURE terminar_prescr_hemodialise (nr_atendimento_p bigint, nr_prescricao_p bigint, dt_inicio_prescr_p timestamp) AS $body$
DECLARE



nr_prescr_anterior_w bigint;



BEGIN

select	max(nr_prescricao)
into STRICT	nr_prescr_anterior_w
from	prescr_medica
where	nr_prescricao < nr_prescricao_p
and	nr_atendimento = nr_atendimento_p
and	coalesce(dt_validade_prescr::text, '') = ''
and	(ie_hemodialise IS NOT NULL AND ie_hemodialise::text <> '');

if (nr_prescr_anterior_w IS NOT NULL AND nr_prescr_anterior_w::text <> '') then
	update	prescr_medica
	set	dt_validade_prescr 	= dt_inicio_prescr_p
	where	nr_prescricao	   	= nr_prescr_anterior_w;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
