CREATE OR REPLACE PROCEDURE tiss_alterar_status_lote_anexo (nr_seq_lote_p bigint, nm_usuario_p text, ie_status_p text) AS $body$
DECLARE


/* ie_status_p
 1 	Pendente
 2	Enviado
 3	Retorno recebido
*/
BEGIN

if (ie_status_p = '1') then

	update	tiss_anexo_lote
	set	dt_envio		 = NULL,
		nm_usuario_envio	 = NULL,
		ie_status		= ie_status_p
	where	nr_sequencia		= nr_seq_lote_p;

elsif (ie_status_p = '2') then

	update	tiss_anexo_lote
	set	dt_envio		= clock_timestamp(),
		nm_usuario_envio	= nm_usuario_p,
		ie_status		= ie_status_p
	where	nr_sequencia		= nr_seq_lote_p;

elsif (ie_status_p = '3') then

	update	tiss_anexo_lote
	set	ie_status		= ie_status_p
	where	nr_sequencia		= nr_seq_lote_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
