CREATE OR REPLACE PROCEDURE tiss_atlzr_recurso_glosa_guia (nr_seq_guia_p bigint, cd_motivo_tiss_p text, ds_justificativa_p text) AS $body$
DECLARE

cd_motivo_tiss_w varchar(4) := cd_motivo_tiss_p;
ds_justificativa_w varchar(200) := ds_justificativa_p;							
							

BEGIN


	if cd_motivo_tiss_w = '' or cd_motivo_tiss_w = '0' then
		cd_motivo_tiss_w := null;
	end if;
	
	if ds_justificativa_w = '' then
		ds_justificativa_w := null;
	end if;
	
	update 	tiss_recurso_glosa_guia
	set    	cd_motivo_tiss   = cd_motivo_tiss_w,
		ds_justificativa = ds_justificativa_w,
		nm_usuario	 = wheb_usuario_pck.get_nm_usuario,
		dt_atualizacao	 = clock_timestamp()
	where	nr_sequencia = nr_seq_guia_p;
	commit;
	

end;
$body$
