CREATE OR REPLACE PROCEDURE tiss_atualizar_conta_reap ( nr_seq_conta_reap_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_procedimento_w			double precision;
vl_opm_w			double precision;
vl_gases_w			double precision;
vl_medicamento_w			double precision;
vl_material_w			double precision;
vl_taxa_w			double precision;
vl_diaria_w			double precision;
vl_despesa_w			double precision;
ie_tipo_despesa_w			varchar(5);

c01 CURSOR FOR
SELECT	sum(coalesce(a.vl_despesa,0)),
	ie_tipo_despesa
from	tiss_reap_desp a
where	a.nr_seq_conta	= nr_seq_conta_reap_p
group by	ie_tipo_despesa;


BEGIN

vl_diaria_w	:= 0;
vl_taxa_w	:= 0;
vl_material_w	:= 0;
vl_medicamento_w	:= 0;
vl_gases_w	:= 0;

select	sum(coalesce(a.vl_procedimento,0))
into STRICT	vl_procedimento_w
from	tiss_reap_proc a
where	a.nr_seq_conta	= nr_seq_conta_reap_p;

select	sum(coalesce(a.vl_opm,0))
into STRICT	vl_opm_w
from	tiss_reap_opm a
where	a.nr_seq_conta	= nr_seq_conta_reap_p;

open c01;
loop
fetch c01 into
	vl_despesa_w,
	ie_tipo_despesa_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	if (ie_tipo_despesa_w = 1) then
		vl_gases_w	:= coalesce(vl_gases_w,0) + coalesce(vl_despesa_w,0);
	elsif (ie_tipo_despesa_w = 2) then
		vl_medicamento_w	:= coalesce(vl_medicamento_w,0) + coalesce(vl_despesa_w,0);
	elsif (ie_tipo_despesa_w = 3) then
		vl_material_w	:= coalesce(vl_material_w,0) + coalesce(vl_despesa_w,0);
	elsif (ie_tipo_despesa_w = 4) or (ie_tipo_despesa_w = 6) then
		vl_taxa_w	:= coalesce(vl_taxa_w,0) + coalesce(vl_despesa_w,0);
	elsif (ie_tipo_despesa_w = 5) then
		vl_diaria_w	:= coalesce(vl_diaria_w,0) + coalesce(vl_despesa_w,0);
	end if;

end loop;
close c01;

update	tiss_reap_conta
set 	vl_gases		= coalesce(vl_gases_w, 0),
	vl_medicamento	= coalesce(vl_medicamento_w,0),
	vl_procedimento	= coalesce(vl_procedimento_w,0),
	vl_material	= coalesce(vl_opm_w,0) + coalesce(vl_material_w,0),
	vl_diaria		= coalesce(vl_diaria_w,0),
	vl_taxa		= coalesce(vl_taxa_w,0),
	vl_total		= coalesce(vl_gases_w,0) + coalesce(vl_medicamento_w,0) + coalesce(vl_procedimento_w,0) + coalesce(vl_opm_w,0) + coalesce(vl_material_w,0) + coalesce(vl_diaria_w,0) + coalesce(vl_taxa_w,0)
where	nr_sequencia	= nr_seq_conta_reap_p;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
