CREATE OR REPLACE PROCEDURE tiss_atualizar_lote_reap ( nr_seq_lote_reap_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_diaria_w		double precision;
vl_gases_w		double precision;
vl_material_w		double precision;
vl_medicamento_w		double precision;
vl_procedimento_w		double precision;
vl_taxa_w		double precision;
vl_total_w		double precision;
vl_total_honorario_w	double precision;
nr_seq_reap_conta_w	bigint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	tiss_reap_conta
where	nr_seq_lote	= nr_seq_lote_reap_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_reap_conta_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	CALL tiss_atualizar_conta_reap(nr_seq_reap_conta_w, nm_usuario_p);

end loop;
close c01;

select	sum(coalesce(vl_diaria,0)),
	sum(coalesce(vl_gases,0)),
	sum(coalesce(vl_material,0)),
	sum(coalesce(vl_medicamento,0)),
	sum(coalesce(vl_procedimento,0)),
	sum(coalesce(vl_taxa,0)),
	sum(coalesce(vl_total,0)),
	sum(coalesce(vl_total_honorario,0))
into STRICT	vl_diaria_w,
	vl_gases_w,
	vl_material_w,
	vl_medicamento_w,
	vl_procedimento_w,
	vl_taxa_w,
	vl_total_w,
	vl_total_honorario_w
from	tiss_reap_conta
where	nr_seq_lote	= nr_seq_lote_reap_p;

update	tiss_reap_lote
set	vl_diaria		= vl_diaria_w,
	vl_gases		= vl_gases_w,
	vl_material	= vl_material_w,
	vl_medicamento	= vl_medicamento_w,
	vl_procedimento	= vl_procedimento_w,
	vl_taxa		= vl_taxa_w,
	vl_total		= vl_total_w,
	vl_total_honorario	= vl_total_honorario_w
where	nr_sequencia	= nr_seq_lote_reap_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
