CREATE OR REPLACE PROCEDURE tiss_atualiza_autorizacao_conv ( nr_sequencia_p bigint, cd_senha_p text, nr_seq_estagio_p bigint, qt_dia_autorizado_p bigint, dt_entrada_prevista_p timestamp, cd_autorizacao_p text, dt_validade_guia_p timestamp) AS $body$
DECLARE



nm_usuario_w	varchar(15) := wheb_usuario_pck.Get_Nm_Usuario;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	update	autorizacao_convenio
	set	cd_senha 		= coalesce(cd_senha, cd_senha_p),
		nr_seq_estagio 		= nr_seq_estagio_p,
		qt_dia_autorizado 		= qt_dia_autorizado_p,
		dt_entrada_prevista 	= dt_entrada_prevista_p,
		cd_autorizacao 		= coalesce(cd_autorizacao, cd_autorizacao_p),
		nm_usuario		= nm_usuario_w,
		dt_atualizacao  		= clock_timestamp(),
		dt_validade_guia		= coalesce(dt_validade_guia_p,dt_validade_guia)
	where	nr_sequencia 		= nr_sequencia_p;
	commit;

end if;

end;
$body$
