CREATE OR REPLACE PROCEDURE tiss_baca_acerto_diag () AS $body$
DECLARE


nr_atendimento_w		bigint;
dt_diagnostico_w		timestamp;
ie_tipo_doenca_w		varchar(20);
ie_unidade_tempo_w		varchar(20);
qt_tempo_w			bigint;

c01 CURSOR FOR
SELECT	nr_atendimento,
	dt_diagnostico,
	ie_tipo_doenca,
	ie_unidade_tempo,
	qt_tempo
from	diagnostico_medico
where	(ie_tipo_doenca IS NOT NULL AND ie_tipo_doenca::text <> '')
or	(ie_unidade_tempo IS NOT NULL AND ie_unidade_tempo::text <> '')
or	(qt_tempo IS NOT NULL AND qt_tempo::text <> '');


BEGIN

open c01;
loop
fetch c01 into
	nr_atendimento_w,
	dt_diagnostico_w,
	ie_tipo_doenca_w,
	ie_unidade_tempo_w,
	qt_tempo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	update	diagnostico_doenca
	set	ie_tipo_doenca		= coalesce(ie_tipo_doenca, ie_tipo_doenca_w),
		ie_unidade_tempo	= coalesce(ie_unidade_tempo, ie_unidade_tempo_w),
		qt_tempo		= coalesce(qt_tempo, qt_tempo_w)
	where	nr_atendimento		= nr_atendimento_w
	and	dt_diagnostico		= dt_diagnostico_w;

	commit;

end loop;
close c01;

end;
