CREATE OR REPLACE PROCEDURE tiss_converte_membro_equipe ( cd_estabelecimento_p bigint, cd_convenio_p bigint, nm_usuario_p text, cd_medico_executor_p text, cd_setor_atendimento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_funcao_medico_p text, ie_responsavel_credito_p text, dt_procedimento_p timestamp, cd_medico_exec_tiss_p INOUT text) AS $body$
DECLARE


cd_medico_exec_tiss_w	varchar(255);
cd_grupo_proc_w		bigint;
cd_area_procedimento_w	bigint;
cd_especialidade_w	bigint;
count_w			bigint;

c01 CURSOR FOR
SELECT 	cd_medico_tiss
from 	tiss_regra_equipe
where	cd_estabelecimento						= cd_estabelecimento_p
and	cd_convenio							= cd_convenio_p
and 	coalesce(cd_pessoa_fisica, coalesce(cd_medico_executor_p, 0))		= coalesce(cd_medico_executor_p, 0)
and	coalesce(cd_procedimento, coalesce(cd_procedimento_p,0))			= coalesce(cd_procedimento_p,0)
and	((ie_origem_proced = ie_origem_proced_p) or (coalesce(ie_origem_proced::text, '') = ''))
and	coalesce(cd_area_procedimento, coalesce(cd_area_procedimento_w,0)) 	= coalesce(cd_area_procedimento_w,0)
and	coalesce(cd_especialidade, coalesce(cd_especialidade_w,0))		= coalesce(cd_especialidade_w,0)
and	coalesce(cd_grupo_proc, coalesce(cd_grupo_proc_w,0))			= coalesce(cd_grupo_proc_w,0)
and	coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_p, 0)) 	= coalesce(cd_setor_atendimento_p, 0)
and	coalesce(ie_funcao_medico, coalesce(ie_funcao_medico_p,'X'))		= coalesce(ie_funcao_medico_p, 'X')
and	coalesce(ie_responsavel_credito, coalesce(ie_responsavel_credito_p,'X'))	= coalesce(ie_responsavel_credito_p,'X')
and	coalesce(ie_dia_semana,coalesce(pkg_date_utils.get_WeekDay(dt_procedimento_p),'0'))	= coalesce(pkg_date_utils.get_WeekDay(dt_procedimento_p),'0')
and	(((coalesce(hr_final::text, '') = '') or (coalesce(hr_inicial::text, '') = '')) or (coalesce(dt_procedimento_p,clock_timestamp()) between ESTABLISHMENT_TIMEZONE_UTILS.DateAtTime(coalesce(dt_procedimento_p,clock_timestamp()), coalesce(to_char(hr_inicial,'hh24:mi:ss'), '00:00:01'))	and
	ESTABLISHMENT_TIMEZONE_UTILS.DateAtTime(coalesce(dt_procedimento_p,clock_timestamp()), coalesce(to_char(hr_final,'hh24:mi:ss'), '23:59:59'))))
order by coalesce(cd_procedimento, 0),
	 coalesce(cd_grupo_proc, 0),
	 coalesce(cd_especialidade,0),
	 coalesce(cd_area_procedimento, 0),
	 coalesce(cd_setor_atendimento, 0),
	 coalesce(cd_pessoa_fisica, 0),
	 coalesce(ie_funcao_medico,'X'),
	 coalesce(ie_responsavel_credito,'X'),
	 coalesce(ie_dia_semana,'X');


BEGIN

select 	count(*)
into STRICT	count_w
from 	tiss_regra_equipe
where	cd_estabelecimento	= cd_estabelecimento_p
and	cd_convenio		= cd_convenio_p;

cd_medico_exec_tiss_p	:= null;

if (count_w > 0) then

	select	max(a.cd_grupo_proc),
		max(substr(OBTER_AREA_PROCEDIMENTO(cd_procedimento_p, ie_origem_proced_p),1,200)),
		max(substr(OBTER_ESPECIALIDADE_PROCED(cd_procedimento_p, ie_origem_proced_p),1,200))
	into STRICT	cd_grupo_proc_w,
		cd_area_procedimento_w,
		cd_especialidade_w
	from	procedimento a
	where	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p;

	if (cd_medico_executor_p IS NOT NULL AND cd_medico_executor_p::text <> '') then
		open c01;
		loop
		fetch c01 into
			cd_medico_exec_tiss_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			cd_medico_exec_tiss_p := cd_medico_exec_tiss_w;	
		end loop;
		close c01;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
