cd_autorizacao_w 	varchar(255);

BEGIN
 
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_seq_pedido_p IS NOT NULL AND nr_seq_pedido_p::text <> '') and (nr_seq_pedido_exame_p IS NOT NULL AND nr_seq_pedido_exame_p::text <> '') then 
	begin 
	SELECT * FROM tiss_obter_guia( 
		'4', null, cd_autorizacao_w, 'N', null, null, null, null, null, ie_guia_w, nr_seq_pedido_p, null, null, null, null, null, null, null, null, null, null, null, null, null, null, nr_seq_pedido_exame_p) INTO STRICT cd_autorizacao_w, ie_guia_w;	
	 
	update	pedido_exame_externo_item 
	set	nr_doc_convenio	= cd_autorizacao_w, 
		nm_usuario	= nm_usuario_p, 
		dt_atualizacao	= clock_timestamp() 
	where	nr_sequencia	= nr_seq_pedido_exame_p 
	and	nr_seq_pedido	= nr_seq_pedido_p;
	 
