CREATE OR REPLACE PROCEDURE tiss_gerar_cabecalho_analise ( nr_seq_retorno_p bigint, nr_seq_lote_hist_p bigint, ds_tipo_transacao_p text, nr_seq_transacao_p bigint, cd_ans_origem_p text, cd_cgc_prestador_p text, cd_prestador_convenio_p text, ds_versao_p text, ds_arquivo_p text, nm_usuario_p text, nr_seq_cabecalho_p INOUT bigint) AS $body$
DECLARE


nr_sequencia_w	bigint;
nr_seq_lote_w	bigint;
qt_reg_w		bigint := 0;


BEGIN

select	count(1)
into STRICT	qt_reg_w
from	pessoa_juridica
where	cd_cgc = cd_cgc_prestador_p  LIMIT 1;

if (qt_reg_w = 0) then
	begin
	CALL wheb_mensagem_pck.exibir_mensagem_abort(454916);
	end;
end if;

select	nextval('tiss_cabecalho_seq')
into STRICT	nr_sequencia_w
;

insert into tiss_cabecalho(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	nr_seq_retorno,
	nr_seq_lote_hist,
	ds_tipo_transacao,
	nr_seq_transacao,
	cd_ans_origem,
	cd_cgc_prestador,
	cd_prestador_convenio,
	ds_versao,
	ds_arquivo)
values (	nr_sequencia_w,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_retorno_p,
	nr_seq_lote_hist_p,
	ds_tipo_transacao_p,
	nr_seq_transacao_p,
	cd_ans_origem_p,
	cd_cgc_prestador_p,
	cd_prestador_convenio_p,
	ds_versao_p,
	ds_arquivo_p);

nr_seq_cabecalho_p := nr_sequencia_w;

if (coalesce(nr_seq_lote_hist_p,0) > 0) then
	select 	max(nr_sequencia)
	into STRICT 	nr_seq_lote_w
	from 	lote_auditoria
	where 	nr_sequencia in (SELECT nr_seq_lote_audit from lote_audit_hist where nr_sequencia = nr_seq_lote_hist_p)
	and 	coalesce(dt_fechamento::text, '') = ''
	and 	coalesce(nr_proceso::text, '') = '';

	if (coalesce(nr_seq_lote_w,0) > 0) then
		update lote_auditoria set nr_proceso = nr_seq_transacao_p where nr_sequencia = nr_seq_lote_w;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
