CREATE OR REPLACE PROCEDURE tiss_gerar_cabec_solic_opme (nr_sequencia_autor_p bigint, nr_seq_anexo_p bigint, nm_usuario_p text, nr_seq_guia_nova_p INOUT bigint) AS $body$
DECLARE


cd_convenio_w			bigint;
cd_estabelecimento_w		bigint;
cd_ans_w			varchar(100);
ds_arquivo_logo_w		varchar(255);
ds_arquivo_logo_comp_w		varchar(255);
ie_gerar_tiss_w			varchar(10);
nr_seq_anexo_w			bigint;
nr_guia_operadora_w		varchar(20);
nr_guia_anexo_w			varchar(20);
nr_guia_principal_w		varchar(20);
cd_senha_w			varchar(20);
dt_autorizacao_w		timestamp;
cd_usuario_convenio_w		varchar(20);
nm_pessoa_fisica_w		varchar(255);
nm_contratado_w			varchar(255);
nr_telefone_contrat_w		varchar(11);
ds_email_contrat_w		varchar(60);
ds_justificativa_w		varchar(1000);
ds_especific_mat_w		varchar(500);
nr_seq_guia_w			bigint;
ds_versao_w			varchar(20);
cd_pessoa_fisica_w		varchar(10);
nr_atendimento_w		bigint;

c01 CURSOR FOR
SELECT	nr_sequencia,
	nr_guia_operadora,
	nr_guia_anexo,
	nr_guia_principal,
	cd_senha,
	dt_autorizacao,
	cd_usuario_convenio,
	nm_pessoa_fisica,
	nm_contratado,
	nr_telefone_contrat,
	ds_email_contrat,
	ds_justificativa,
	ds_especific_mat
from	tiss_anexo_guia
where	nr_sequencia_autor	= nr_sequencia_autor_p
and	nr_sequencia		= nr_seq_anexo_p
and	ie_tiss_tipo_anexo	= 1; --OPME
BEGIN

if (coalesce(nr_sequencia_autor_p,-1) > 0) then

	select	max(a.cd_convenio),
		max(a.cd_estabelecimento),
		max(c.cd_ans),
		max(b.ds_arquivo_logo_tiss),
		max(a.cd_pessoa_fisica),
		max(a.nr_atendimento)
	into STRICT	cd_convenio_w,
		cd_estabelecimento_w,
		cd_ans_w,
		ds_arquivo_logo_w,
		cd_pessoa_fisica_w,
		nr_atendimento_w
	from	autorizacao_convenio a,
		convenio b,
		pessoa_juridica c
	where	b.cd_convenio	= a.cd_convenio
	and	b.cd_cgc	= c.cd_cgc
	and	a.nr_sequencia	= nr_sequencia_autor_p;

	select	max(ds_arquivo_logo_comp),
		coalesce(max(ie_gerar_tiss), 'S')
	into STRICT	ds_arquivo_logo_comp_w,
		ie_gerar_tiss_w
	from	tiss_parametros_convenio
	where	cd_estabelecimento	= cd_estabelecimento_w
	and	cd_convenio		= cd_convenio_w;

	select	max(ds_versao)
	into STRICT	ds_versao_w
	from	xml_projeto
	where	nr_sequencia	= tiss_obter_xml_projeto(cd_convenio_w, cd_estabelecimento_w, '21');

	open C01;
	loop
	fetch C01 into
		nr_seq_anexo_w,
		nr_guia_operadora_w,
		nr_guia_anexo_w,
		nr_guia_principal_w,
		cd_senha_w,
		dt_autorizacao_w,
		cd_usuario_convenio_w,
		nm_pessoa_fisica_w,
		nm_contratado_w,
		nr_telefone_contrat_w,
		ds_email_contrat_w,
		ds_justificativa_w,
		ds_especific_mat_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	nextval('w_tiss_guia_seq')
		into STRICT	nr_seq_guia_w
		;

		insert into w_tiss_guia(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			cd_autorizacao,
			nr_guia_prestador,
			ds_versao,
			cd_autorizacao_princ,
			cd_senha,
			dt_autorizacao,
			cd_ans,
			ds_justificativa,
			ds_especific_mat,
			ie_tiss_tipo_guia,
			nr_atendimento,
			ds_observacao)
		values (nr_seq_guia_w,
			clock_timestamp(),
			nm_usuario_p,
			nr_guia_operadora_w,
			nr_guia_anexo_w,
			ds_versao_w,
			nr_guia_principal_w,
			cd_senha_w,
			dt_autorizacao_w,
			cd_ans_w,
			ds_justificativa_w,
			ds_especific_mat_w,
			'9',
			nr_atendimento_w,
			ds_especific_mat_w);

		insert	into w_tiss_beneficiario(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_guia,
			cd_pessoa_fisica,
			nm_pessoa_fisica,
			cd_usuario_convenio)
		values (nextval('w_tiss_beneficiario_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_guia_w,
			cd_pessoa_fisica_w,
			nm_pessoa_fisica_w,
			cd_usuario_convenio_w);

		insert 	into w_tiss_contratado_solic(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			nr_seq_guia,
			nm_contratado,
			nr_telefone_contrat,
			ds_email_contrat)
		values (nextval('w_tiss_contratado_solic_seq'),
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_guia_w,
			nm_contratado_w,
			nr_telefone_contrat_w,
			ds_email_contrat_w);


		end;
	end loop;
	close C01;

end if;

nr_seq_guia_nova_p	:= nr_seq_guia_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE tiss_gerar_cabec_solic_opme (nr_sequencia_autor_p bigint, nr_seq_anexo_p bigint, nm_usuario_p text, nr_seq_guia_nova_p INOUT bigint) FROM PUBLIC;

