CREATE OR REPLACE PROCEDURE tiss_gerar_conta ( nr_interno_conta_p bigint, nr_seq_med_fatur_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_atend_med_p bigint, nr_seq_prot_med_p bigint) AS $body$
BEGIN

if (coalesce(nr_interno_conta_p,0) > 0) then

	delete	from tiss_Conta_Atend
	where	nr_interno_conta	= nr_interno_conta_p;

	delete	from tiss_Conta_Proc
	where	nr_interno_conta	= nr_interno_conta_p;

	delete	from w_tiss_Conta_Proc
	where	nr_interno_conta	= nr_interno_conta_p;

	delete	from tiss_Conta_partic
	where	nr_interno_conta	= nr_interno_conta_p;

	delete	from tiss_Conta_Desp
	where	nr_interno_conta	= nr_interno_conta_p;

	delete	from tiss_conta_Opm_Exec
	where	nr_interno_conta	= nr_interno_conta_p;

	delete	from tiss_Conta_Guia
	where	nr_interno_conta	= nr_interno_conta_p;

	update	tiss_conta_guia_regra
	set	nr_seq_guia  = NULL
	where	nr_interno_conta = nr_interno_conta_p;

else
	delete	from tiss_Conta_Atend
	where	nr_atend_med	= nr_atend_med_p
	and	nr_seq_prot_med	= nr_seq_prot_med_p;

	delete	from tiss_Conta_Proc
	where	nr_atend_med	= nr_atend_med_p
	and	nr_seq_prot_med	= nr_seq_prot_med_p;

	delete	from tiss_Conta_Desp
	where	nr_atend_med	= nr_atend_med_p
	and	nr_seq_prot_med	= nr_seq_prot_med_p;

	delete	from tiss_Conta_Guia
	where	nr_atend_med	= nr_atend_med_p
	and	nr_seq_prot_med	= nr_seq_prot_med_p;
end if;

CALL Tiss_Gerar_Conta_Atend(nr_interno_conta_p, nr_seq_med_fatur_p, cd_estabelecimento_p, nm_usuario_p, nr_atend_med_p, nr_seq_prot_med_p);

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

CALL Tiss_Gerar_Conta_Proc(nr_interno_conta_p, nr_seq_med_fatur_p, cd_estabelecimento_p, nm_usuario_p, nr_atend_med_p, nr_seq_prot_med_p);

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

CALL tiss_atualizar_guia_desp(null, nr_interno_conta_p, nm_usuario_p);

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

CALL TISS_Gerar_Conta_OPM_Exec(nr_interno_conta_p, nm_usuario_p);

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

CALL Tiss_Gerar_Conta_Mat(nr_interno_conta_p, nr_seq_med_fatur_p, nm_usuario_p, nr_atend_med_p, nr_seq_prot_med_p);

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

CALL Tiss_Gerar_Conta_Guia(nr_interno_conta_p, nr_seq_med_fatur_p, cd_estabelecimento_p, nm_usuario_p, nr_atend_med_p, nr_seq_prot_med_p);

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
