CREATE OR REPLACE PROCEDURE tiss_gerar_reap ( nr_seq_reap_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_conta_w			bigint;

c01 CURSOR FOR 
SELECT	coalesce(nr_sequencia,0) 
from	tiss_reap_conta 
where	nr_seq_lote		= nr_seq_reap_lote_p;


BEGIN 
 
open c01;
loop 
fetch c01 into 
	nr_seq_conta_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	 
	delete 
	from	tiss_conta_proc 
	where	nr_seq_reap_conta	= nr_seq_conta_w;
	 
	delete 
	from	tiss_conta_atend 
	where	nr_seq_reap_conta	= nr_seq_conta_w;
	 
	delete 
	from	tiss_conta_guia 
	where	nr_seq_reap_conta	= nr_seq_conta_w;
	 
	delete 
	from	tiss_conta_opm_exec 
	where	nr_seq_reap_conta	= nr_seq_conta_w;
	 
	delete 
	from	tiss_conta_desp 
	where	nr_seq_reap_conta	= nr_seq_conta_w;
	 
	CALL tiss_reap_atualizar_desp(nr_seq_conta_w, nm_usuario_p);	
	CALL tiss_gerar_reap_atend(nr_seq_conta_w, nm_usuario_p);
	CALL tiss_gerar_reap_proc(nr_seq_conta_w, nm_usuario_p);
	CALL tiss_gerar_reap_guia(nr_seq_conta_w, nm_usuario_p);
	CALL tiss_gerar_reap_opm(nr_seq_conta_w, nm_usuario_p);
	CALL tiss_gerar_reap_desp(nr_seq_conta_w, nm_usuario_p);
	 
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
