CREATE OR REPLACE PROCEDURE tiss_gravar_inconsistencia (nr_seq_inconsistencia_p bigint, nr_sequencia_autor_p bigint, nr_interno_conta_p bigint, nr_seq_protocolo_p bigint, nm_usuario_p text, ds_complemento_p text) AS $body$
DECLARE


cont_w		bigint;


BEGIN

if (coalesce(nr_sequencia_autor_p,0) > 0) then
	begin
	select	1
	into STRICT	cont_w
	from	w_tiss_inconsistencia
	where	nr_seq_inconsistencia		= nr_seq_inconsistencia_p
	and	nr_sequencia_autor		= nr_sequencia_autor_p
	and	ds_complemento			= ds_complemento_p  LIMIT 1;
	exception
	when others then
		cont_w	:= 0;
	end;
elsif (coalesce(nr_interno_conta_p,0) > 0) then
	begin
	select	1
	into STRICT	cont_w
	from	w_tiss_inconsistencia
	where	nr_seq_inconsistencia		= nr_seq_inconsistencia_p
	and	nr_interno_conta		= nr_interno_conta_p
	and	ds_complemento			= ds_complemento_p  LIMIT 1;
	exception
	when others then
		cont_w	:= 0;
	end;
elsif (coalesce(nr_seq_protocolo_p,0) > 0) then
	begin
	select	1
	into STRICT	cont_w
	from	w_tiss_inconsistencia
	where	nr_seq_inconsistencia		= nr_seq_inconsistencia_p
	and	nr_seq_protocolo		= nr_seq_protocolo_p
	and	ds_complemento			= ds_complemento_p  LIMIT 1;
	exception
	when others then
		cont_w	:= 0;
	end;
end if;

if (cont_w = 0) then
	insert into w_tiss_inconsistencia(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_inconsistencia,
		nr_sequencia_autor,
		nr_interno_conta,
		nr_seq_protocolo,
		ds_complemento)
	SELECT	nextval('w_tiss_inconsistencia_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_inconsistencia_p,
		nr_sequencia_autor_p,
		nr_interno_conta_p,
		nr_seq_protocolo_p,
		ds_complemento_p
	;

	if (coalesce(tiss_convenio_pck.get_gravar_log_inconsist,'N') = 'S') then
		CALL tiss_convenio_pck.gravar_log_inconsist(nm_usuario_p, nr_seq_inconsistencia_p, nr_sequencia_autor_p, nr_interno_conta_p, nr_seq_protocolo_p);
	end if;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
