CREATE OR REPLACE PROCEDURE tiss_obter_regra_exec_compl (cd_convenio_p bigint, cd_estabelecimento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_area_procedimento_p bigint, cd_especialidade_p bigint, cd_grupo_proc_p bigint, ie_tipo_atendimento_p bigint, cd_setor_atendimento_p bigint, ie_responsavel_credito_p text, ie_tiss_tipo_guia_p text, cd_medico_exec_p text, ie_envio_p INOUT text, cd_medico_exe_regra_p INOUT text, cd_setor_entrada_p bigint, cd_setor_exec_proc_p bigint) AS $body$
DECLARE


ie_envio_w		varchar(3);
cd_medico_prest_w 	varchar(255);

c01 CURSOR FOR
SELECT	coalesce(ie_envio, 'S'),
	cd_medico_prest
from	tiss_regra_exec_compl
where	cd_convenio							= cd_convenio_p
and	cd_estabelecimento						= cd_estabelecimento_p
and	coalesce(cd_procedimento, coalesce(cd_procedimento_p, 0))			= coalesce(cd_procedimento_p, 0)
and	coalesce(ie_origem_proced, coalesce(ie_origem_proced_p,0))		= coalesce(ie_origem_proced_p,0)
and	coalesce(cd_area_procedimento, coalesce(cd_area_procedimento_p,0))	= coalesce(cd_area_procedimento_p,0)
and	coalesce(cd_especialidade, coalesce(cd_especialidade_p,0))		= coalesce(cd_especialidade_p,0)
and	coalesce(cd_grupo_proc,	 coalesce(cd_grupo_proc_p,0))		= coalesce(cd_grupo_proc_p,0)
and	coalesce(ie_tipo_atendimento, coalesce(ie_tipo_atendimento_p,0))		= coalesce(ie_tipo_atendimento_p,0)
and	coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_p,0)) 	= coalesce(cd_setor_atendimento_p,0)
and	coalesce(ie_responsavel_credito, coalesce(ie_responsavel_credito_p, 'X'))	= coalesce(ie_responsavel_credito_p, 'X')
and	coalesce(ie_tiss_tipo_guia, coalesce(ie_tiss_tipo_guia_p,'4'))		= coalesce(ie_tiss_tipo_guia_p, '4')
and 	coalesce(cd_medico_executor, coalesce(cd_medico_exec_p, 'X'))		= coalesce(cd_medico_exec_p, 'X')
and	coalesce(cd_setor_entrada, coalesce(cd_setor_entrada_p,0)) 		= coalesce(cd_setor_entrada_p,0)
and	coalesce(cd_setor_exec_proc, coalesce(cd_setor_exec_proc_p,0))		= coalesce(cd_setor_exec_proc_p,0)
and	clock_timestamp() between coalesce(dt_inicio_vigencia,to_date('01/01/1990','dd/mm/yyyy')) and ESTABLISHMENT_TIMEZONE_UTILS.endOfDay(coalesce(dt_fim_vigencia,clock_timestamp() + interval '1 days'))
order by	coalesce(cd_procedimento, 0),
	coalesce(cd_grupo_proc, 0),
	coalesce(cd_especialidade, 0),
	coalesce(cd_area_procedimento, 0),
	coalesce(ie_tipo_atendimento, 0),
	coalesce(cd_setor_atendimento, 0),
	coalesce(cd_medico_executor,'X'),
	coalesce(ie_responsavel_credito, 'X') desc, --lhalves OS283189 em 01/02/2011 - Ordenar por resp credito
	coalesce(cd_setor_entrada,0),
	coalesce(dt_inicio_vigencia,to_date('01/01/1990','dd/mm/yyyy')),
	coalesce(cd_setor_exec_proc,0);


BEGIN

open c01;
loop
fetch c01 into
	ie_envio_w,
	cd_medico_prest_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

ie_envio_p		:= coalesce(ie_envio_w, 'ME');
cd_medico_exe_regra_p	:= cd_medico_prest_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
