CREATE OR REPLACE PROCEDURE tiss_obter_regra_guia_opme (cd_estabelecimento_p bigint, cd_cgc_prestador_p bigint, cd_convenio_p bigint, ie_tipo_atendimento_p bigint, ie_tipo_despesa_p text, cd_medico_exec_tiss_p bigint, ie_tiss_tipo_guia_atual_p text, ie_tiss_tipo_guia_retorno_p INOUT text, cd_setor_atendimento_p bigint, cd_categoria_conv_p text, cd_grupo_material_p bigint, cd_subgrupo_material_p bigint, cd_classe_material_p bigint, cd_material_p bigint, cd_area_procedimento_p bigint, cd_especialidade_p bigint, cd_grupo_proc_p bigint, cd_procedimento_p bigint) AS $body$
DECLARE


ie_guia_ref_w		varchar(15);
ie_tiss_tipo_guia_w	varchar(10);
ds_versao_w		varchar(20);

c01 CURSOR FOR
SELECT	a.ie_guia_ref
from	tiss_regra_guia_opm a
where	a.cd_estabelecimento						= cd_estabelecimento_p
and	coalesce(a.cd_convenio, coalesce(cd_convenio_p, 0))			= coalesce(cd_convenio_p, 0)
and 	coalesce(a.cd_cgc_prestador,coalesce(cd_cgc_prestador_p, 0))		= coalesce(cd_cgc_prestador_p, 0)
and 	coalesce(a.cd_medico_executor,coalesce(cd_medico_exec_tiss_p, 0))		= coalesce(cd_medico_exec_tiss_p, 0)
and 	coalesce(a.ie_tipo_atendimento,coalesce(ie_tipo_atendimento_p, 0)) 	= coalesce(ie_tipo_atendimento_p, 0)
and	coalesce(a.cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0))	= coalesce(cd_setor_atendimento_p, 0)
and	coalesce(a.cd_categoria, coalesce(cd_categoria_conv_p,'X'))		= coalesce(cd_categoria_conv_p, 'X')
and (coalesce(a.cd_procedimento::text, '') = '' or a.cd_procedimento = cd_procedimento_p)
and (coalesce(a.cd_area_procedimento::text, '') = '' or a.cd_area_procedimento = cd_area_procedimento_p)
and (coalesce(a.cd_especialidade::text, '') = '' or a.cd_especialidade = cd_especialidade_p)
and (coalesce(a.cd_grupo_proc::text, '') = '' or a.cd_grupo_proc = cd_grupo_proc_p)
and (coalesce(a.cd_material::text, '') = '' or a.cd_material = cd_material_p)
and (coalesce(a.cd_grupo_material::text, '') = '' or a.cd_grupo_material = cd_grupo_material_p)
and (coalesce(a.cd_subgrupo_material::text, '') = '' or a.cd_subgrupo_material = cd_subgrupo_material_p)
and (coalesce(a.cd_classe_material::text, '') = '' or a.cd_classe_material = cd_classe_material_p)
and	(((obter_se_projeto_versao(0,12,ds_versao_w,0) = 'N') and	
	  ((a.ie_taxas = 'S') or (a.ie_taxas = 'N' and (ie_tipo_despesa_p <> '4'))) and
	  ((a.ie_gases = 'S') or
	   (a.ie_gases = 'N' AND ie_tipo_despesa_p <> '1')) and	
	  ((a.ie_medicamentos = 'S') or
	   (a.ie_medicamentos = 'N' AND ie_tipo_despesa_p <> '2')) and
	  ((a.ie_materiais = 'S') or
	   (a.ie_materiais = 'N' AND ie_tipo_despesa_p <> '3')) and	
	  ((a.ie_diarias = 'S') or
	   (a.ie_diarias = 'N' AND ie_tipo_despesa_p <> '5')) and	
	  ((a.ie_aluguel = 'S') or
	   (a.ie_aluguel = 'N' AND ie_tipo_despesa_p <> '6')) and 	
	  ((a.ie_opm = 'S') or (a.ie_opm = 'N' and ie_tipo_despesa_p not in ('7','8','9'))))
	or /*Tratamento para a versao 3.00.01 onde o tipo de despesa e diferente*/
	 ((obter_se_projeto_versao(0,12,ds_versao_w,0) = 'S') and	
	  ((a.ie_taxas = 'S') or (a.ie_taxas = 'N' and (ie_tipo_despesa_p <> '7'))) and
	  ((a.ie_gases = 'S') or
	   (a.ie_gases = 'N' AND ie_tipo_despesa_p <> '1')) and	
	  ((a.ie_medicamentos = 'S') or
	   (a.ie_medicamentos = 'N' AND ie_tipo_despesa_p <> '2')) and
	  ((a.ie_materiais = 'S') or
	   (a.ie_materiais = 'N' AND ie_tipo_despesa_p <> '3')) and	
	  ((a.ie_diarias = 'S') or
	   (a.ie_diarias = 'N' AND ie_tipo_despesa_p <> '5')) and	
	  ((a.ie_aluguel = 'S') or
	   (a.ie_aluguel = 'N' AND ie_tipo_despesa_p <> '7')) and 	
	  ((a.ie_opm = 'S') or (a.ie_opm = 'N' and ie_tipo_despesa_p not in ('8'))))
	)	
order by coalesce(a.cd_procedimento,0),
	coalesce(a.cd_grupo_proc,0),
	coalesce(a.cd_especialidade,0),
	coalesce(a.cd_area_procedimento,0),
	coalesce(a.cd_material,0),
	coalesce(a.cd_classe_material,0),
	coalesce(a.cd_subgrupo_material,0),
	coalesce(a.cd_grupo_material,0),
	coalesce(a.cd_cgc_prestador,0),
	coalesce(a.cd_medico_executor,0),
	coalesce(a.ie_tipo_atendimento,0),
	coalesce(a.cd_convenio,0),
	coalesce(a.cd_setor_atendimento,0),
	coalesce(a.cd_categoria,'X');

BEGIN

select	coalesce(max(tiss_obter_versao(cd_convenio_p,cd_estabelecimento_p)),'2.02.03')
into STRICT	ds_versao_w
;

open c01;
loop
fetch c01 into
	ie_guia_ref_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	if (ie_guia_ref_w	= 'SPSADT') then
		ie_tiss_tipo_guia_w	:= '4';
	else
		ie_tiss_tipo_guia_w	:= '5';
	end if;

end loop;
close c01;

ie_tiss_tipo_guia_retorno_p	:= coalesce(ie_tiss_tipo_guia_w, coalesce(ie_tiss_tipo_guia_atual_p, '4'));

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE tiss_obter_regra_guia_opme (cd_estabelecimento_p bigint, cd_cgc_prestador_p bigint, cd_convenio_p bigint, ie_tipo_atendimento_p bigint, ie_tipo_despesa_p text, cd_medico_exec_tiss_p bigint, ie_tiss_tipo_guia_atual_p text, ie_tiss_tipo_guia_retorno_p INOUT text, cd_setor_atendimento_p bigint, cd_categoria_conv_p text, cd_grupo_material_p bigint, cd_subgrupo_material_p bigint, cd_classe_material_p bigint, cd_material_p bigint, cd_area_procedimento_p bigint, cd_especialidade_p bigint, cd_grupo_proc_p bigint, cd_procedimento_p bigint) FROM PUBLIC;

