CREATE OR REPLACE PROCEDURE tiss_obter_regra_honor_spsadt (cd_convenio_p bigint, cd_estabelecimento_p bigint, ie_responsavel_credito_p text, ie_tipo_atendimento_p bigint, cd_setor_entrada_p bigint, ie_prestador_p INOUT text, ie_exec_compl_p INOUT text) AS $body$
