			dt_atualizacao		= clock_timestamp()
		where	(nr_seq_trans_tiss IS NOT NULL AND nr_seq_trans_tiss::text <> '')
		and	cd_convenio		= cd_convenio_p
		and	cd_estabelecimento	= cd_estabelecimento_p;

		nr_seq_trans_tiss_p	:= nr_seq_trans_tiss_w;

	end if;
end if;

end;
