SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE tiss_obter_setar_guia ( nm_usuario_p text, nr_seq_pedido_p bigint) AS $body$
DECLARE


ie_guia_w		varchar(3);
cd_autorizacao_w 		varchar(255);
nr_atendimento_w		bigint;

BEGIN
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_seq_pedido_p IS NOT NULL AND nr_seq_pedido_p::text <> '') then
	begin

		select	max(coalesce(nr_atendimento,0))
		into STRICT	nr_atendimento_w
		from	pedido_exame_externo
		where	nr_sequencia		= nr_seq_pedido_p;

		if (nr_atendimento_w > 0) then
			SELECT * FROM tiss_obter_guia(
				'4', null, cd_autorizacao_w, 'N', null, null, null, null, null, ie_guia_w, nr_seq_pedido_p, null, null, null, null, null, null, null, null, null, null, null, null, null, null) INTO STRICT cd_autorizacao_w, ie_guia_w;

			update	pedido_exame_externo
			set	cd_autorizacao 	= cd_autorizacao_w,
				nm_usuario	= nm_usuario_p,
				dt_atualizacao	= clock_timestamp()
			where	nr_sequencia	= nr_seq_pedido_p;
			commit;
		end if;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
