CREATE OR REPLACE PROCEDURE tiss_obter_se_envia_partic ( cd_convenio_p bigint, cd_estabelecimento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_tiss_tipo_guia_p text, ie_envio_p INOUT text, ie_responsavel_credito_p text, ie_proc_pacote_p text, cd_funcao_medico_p bigint, cd_medico_p text, cd_setor_atendimento_p bigint, nr_seq_proc_interno_p bigint, dt_referencia_p timestamp) AS $body$
DECLARE


ie_enviar_w		varchar(5);
cd_grupo_proc_w		bigint;
cd_especialidade_w	bigint;
cd_area_procedimento_w	bigint;

c01 CURSOR FOR
SELECT	ie_enviar
from	tiss_regra_partic
where	cd_convenio							= cd_convenio_p
and	cd_estabelecimento						= cd_estabelecimento_p
and	coalesce(ie_tiss_tipo_guia, coalesce(ie_tiss_tipo_guia_p,'X'))		= coalesce(ie_tiss_tipo_guia_p,'X')
and	coalesce(cd_procedimento, coalesce(cd_procedimento_p,0))			= coalesce(cd_procedimento_p,0)
and	coalesce(cd_area_procedimento, coalesce(cd_area_procedimento_w,0))	= coalesce(cd_area_procedimento_w,0)
and	coalesce(cd_especialidade, coalesce(cd_especialidade_w,0))		= coalesce(cd_especialidade_w,0)
and	coalesce(cd_grupo_proc, coalesce(cd_grupo_proc_w,0))			= coalesce(cd_grupo_proc_w,0)
and	coalesce(ie_responsavel_credito, coalesce(ie_responsavel_credito_p, 'X'))	= coalesce(ie_responsavel_credito_p, 'X')
and	coalesce(cd_funcao_medico, coalesce(cd_funcao_medico_p,0))		= coalesce(cd_funcao_medico_p,0)
and	((coalesce(ie_pacote, 'T') = 'T') or (coalesce(ie_pacote, 'T') 		= ie_proc_pacote_p))
and	coalesce(cd_medico,coalesce(cd_medico_p,'0'))				= coalesce(cd_medico_p,'0')
and	coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_p,0))	= coalesce(cd_setor_atendimento_p,0)
and	coalesce(nr_seq_proc_interno, coalesce(nr_seq_proc_interno_p,0))		= coalesce(nr_seq_proc_interno_p, 0)
and	coalesce(dt_referencia_p,clock_timestamp()) between coalesce(dt_inicio_vigencia,clock_timestamp() - interval '99999 days') and coalesce(dt_fim_vigencia,clock_timestamp() + interval '99999 days')
order by coalesce(cd_procedimento, 0),
	coalesce(cd_grupo_proc, 0),
	coalesce(cd_especialidade,0),
	coalesce(cd_area_procedimento, 0),
	coalesce(ie_responsavel_credito,0),
	coalesce(cd_funcao_medico,0),
	coalesce(cd_medico,'0'),
	coalesce(cd_setor_atendimento,0),
	coalesce(nr_seq_proc_interno,0),
	dt_inicio_vigencia;


BEGIN

select	max(cd_grupo_proc),
	max(cd_especialidade),
	max(cd_area_procedimento)
into STRICT	cd_grupo_proc_w,
	cd_especialidade_w,
	cd_area_procedimento_w
from	estrutura_procedimento_v
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;

open c01;
loop
fetch c01 into
	ie_enviar_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ie_enviar_w	:= ie_enviar_w;
end loop;
close c01;

ie_envio_p	:= coalesce(ie_enviar_w,'S');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
