CREATE OR REPLACE PROCEDURE tiss_update_denial_data (nr_seq_item_p text, cd_motivo_glosa_p lote_audit_hist_item.cd_motivo_glosa%type, ie_acao_glosa_p lote_audit_hist_item.ie_acao_glosa%type, ds_justificativa_p lote_audit_hist_item.ds_observacao%type, nr_seq_justif_p lote_audit_hist_item.nr_seq_justificativa%type default null) AS $body$
BEGIN
	
	update	lote_audit_hist_item
	set	cd_motivo_glosa = coalesce(cd_motivo_glosa_p, cd_motivo_glosa),
		ie_acao_glosa = coalesce(ie_acao_glosa_p, ie_acao_glosa),
		ds_observacao = coalesce(ds_justificativa_p, ds_observacao),
		nr_seq_justificativa = nr_seq_justif_p
	where	nr_sequencia in (WITH RECURSIVE cte AS (

				SELECT (regexp_substr(nr_seq_item_p,'[^,]+', 1, level))::numeric  nr_sequencia
		 
		(regexp_substr(nr_seq_item_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_seq_item_p, '[^,]+', 1, level))::text <> '')
				  UNION ALL

				SELECT (regexp_substr(nr_seq_item_p,'[^,]+', 1, level))::numeric  nr_sequencia
		 
		(regexp_substr(nr_seq_item_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_seq_item_p, '[^,]+', 1, level))::text <> '')
				 JOIN cte c ON ()

) SELECT * FROM cte;
);

	commit;

end;
$body$
