CREATE OR REPLACE PROCEDURE tof_gerar_meta_alta ( nr_atendimento_p text, nm_usuario_p text ) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_meta_w		bigint;
nr_seq_meta_atend_w bigint;
contador_w			integer;
ie_status_w			varchar(2) := 'A';
ie_tipo_meta_w		varchar(3);

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
			a.nr_seq_meta,
			m.ie_tipo_meta
	from	tof_meta_atend a,
			tof_meta m
	where	m.nr_sequencia = a.nr_seq_meta
	and		nr_atendimento = nr_atendimento_p
	and		coalesce(a.dt_finalizacao::text, '') = ''
	and		m.ie_regra = 'P'
    and     (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
    and     coalesce(a.dt_inativacao::text, '') = '';


BEGIN

ie_status_w := tof_meta_alta_atingida(nr_atendimento_p);

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	nr_seq_meta_w,
	ie_tipo_meta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

			Select  nextval('tof_meta_atend_hor_seq')
			into STRICT	nr_seq_meta_atend_w
			;

			insert into tof_meta_atend_hor(	nr_sequencia,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							nr_seq_meta_atend,
							ie_status,
							dt_geracao,
							ds_observacao,
							dt_liberacao)
						values (	nr_seq_meta_atend_w,
							clock_timestamp(),
							nm_usuario_p,
							clock_timestamp(),
							nm_usuario_p,
							nr_sequencia_w,
							ie_status_w,
							clock_timestamp(),
							'',
							clock_timestamp());

							commit;

			CALL Alterar_status_tof_meta(nr_seq_meta_atend_w, nm_usuario_p);


	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
