CREATE OR REPLACE PROCEDURE tof_gerar_meta_eventos ( nr_atendimento_p text, nr_seq_meta_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_sequencia_w		bigint;
nr_seq_meta_w		bigint;
nr_seq_meta_atend_w bigint;
contador_w			integer;
ie_status_w			varchar(2) := 'N';
ie_tipo_meta_w		varchar(3);

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
			a.nr_seq_meta,
			m.ie_tipo_meta
	from	tof_meta_atend a,
			tof_meta_item i,
			tof_meta m
	where	m.nr_sequencia = a.nr_seq_meta
	and		nr_atendimento = nr_atendimento_p
	and		coalesce(a.dt_finalizacao::text, '') = ''
	and		i.nr_seq_meta = m.nr_sequencia
	and		i.nr_seq_evento = nr_seq_meta_p
	and		m.ie_regra = 'E';


BEGIN


open C01;
loop
fetch C01 into
	nr_sequencia_w,
	nr_seq_meta_w,
	ie_tipo_meta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

			Select  nextval('tof_meta_atend_hor_seq')
			into STRICT	nr_seq_meta_atend_w
			;

			insert into tof_meta_atend_hor(	nr_sequencia,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							nr_seq_meta_atend,
							ie_status,
							dt_geracao,
							ds_observacao)
						values (	nr_seq_meta_atend_w,
							clock_timestamp(),
							nm_usuario_p,
							clock_timestamp(),
							nm_usuario_p,
							nr_sequencia_w,
							ie_status_w,
							clock_timestamp(),
							'');

							commit;

			CALL Alterar_status_tof_meta(nr_seq_meta_atend_w, nm_usuario_p);


	end;
end loop;
close C01;

commit;

end;
$body$
