CREATE OR REPLACE PROCEDURE tof_verificar_meta_interv ( nr_atendimento_p text, nm_usuario_p text, nr_seq_proc_p bigint) AS $body$
DECLARE


contador_w		integer;
nr_sequencia_w		tof_meta_atend.nr_sequencia%type;
ie_status_w		varchar(2);
nr_seq_meta_w		tof_meta_atend.nr_seq_meta%type;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.nr_seq_meta
	from	tof_meta_atend a,
		tof_meta m
	where	m.nr_sequencia = a.nr_seq_meta
	and	nr_atendimento = nr_atendimento_p
	and	ie_regra = 'I'
	and	coalesce(m.ie_situacao,'A') = 'A'
	and	coalesce(a.dt_finalizacao::text, '') = ''
	and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and coalesce(a.dt_inativacao::text, '') = '';	
			

BEGIN

open C01;
loop
fetch C01 into	
	nr_sequencia_w,
	nr_seq_meta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		
		if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
			begin
	
			select	count(*)
			into STRICT	contador_w
			from	tof_meta_item
			where	nr_seq_meta = nr_seq_meta_w
			and	nr_seq_proc = nr_seq_proc_p;
	
			if (contador_w > 0) then
				begin
				ie_status_w := 'A';
				insert into tof_meta_atend_hor(	nr_sequencia,
								dt_atualizacao,
								nm_usuario,
								dt_atualizacao_nrec,
								nm_usuario_nrec,
								nr_seq_meta_atend,
								ie_status,
								dt_geracao,
								ds_observacao,
								dt_liberacao,
								ie_situacao)
							values (	nextval('tof_meta_atend_hor_seq'),
								clock_timestamp(),
								nm_usuario_p,
								clock_timestamp(),
								nm_usuario_p,
								nr_sequencia_w,
								ie_status_w,
								clock_timestamp(),
								'',
								clock_timestamp(),
								'A');
				end;
			end if;
		
			end;
		end if;		
	end;
end loop;
close C01;


if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
