CREATE OR REPLACE PROCEDURE total_extrato_movto_js ( nr_seq_extrato_p bigint, vl_total_p INOUT bigint, vl_nao_conciliado_p INOUT bigint, vl_conciliado_p INOUT bigint) AS $body$
DECLARE


vl_nao_conciliado_w	double precision := 0;
qt_nao_conciliado_w	double precision := 0;
vl_conciliado_w		double precision := 0;
vl_total_w		double precision := 0;


BEGIN

if (nr_seq_extrato_p IS NOT NULL AND nr_seq_extrato_p::text <> '') then
	begin
	select  coalesce(sum(CASE WHEN coalesce(nr_seq_extrato_parcela::text, '') = '' THEN 1  ELSE 0 END ),0) qt_nao_conciliado,
		coalesce(sum(CASE WHEN coalesce(nr_seq_extrato_parcela::text, '') = '' THEN vl_parcela  ELSE 0 END ),0) vl_nao_conciliado,
		coalesce(sum(CASE WHEN coalesce(nr_seq_extrato_parcela::text, '') = '' THEN 0  ELSE vl_parcela END ),0) vl_conciliado
	into STRICT	qt_nao_conciliado_w,
		vl_nao_conciliado_w,
		vl_conciliado_w
	from    extrato_cartao_cr_movto
	where   nr_seq_extrato   =  nr_seq_extrato_p;

	vl_total_w :=	(vl_nao_conciliado_w + vl_conciliado_w);
	end;
end if;
vl_total_p 		:= vl_total_w;
vl_nao_conciliado_p 	:= vl_nao_conciliado_w;
vl_conciliado_p		:= vl_conciliado_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
