CREATE OR REPLACE PROCEDURE total_extrato_parcela_js ( nr_seq_bandeira_p bigint, cd_estabelecimento_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, dt_processamento_p timestamp, ie_dt_proc_p text, vl_nao_conciliado_p INOUT text, vl_conciliado_p INOUT text, vl_total_p INOUT text) AS $body$
DECLARE


vl_nao_conciliado_w	bigint;
vl_conciliado_w		bigint;	
vl_total_w		bigint;	
			

BEGIN
	
select  coalesce(sum(CASE WHEN coalesce(nr_seq_extrato_parcela::text, '') = '' THEN vl_parcela  ELSE 0 END ),0),
	coalesce(sum(CASE WHEN coalesce(nr_seq_extrato_parcela::text, '') = '' THEN 0  ELSE vl_parcela END ),0)
into STRICT 	vl_nao_conciliado_w,
	vl_conciliado_w		
from (SELECT a.nr_sequencia,
		b.dt_transacao,
		a.dt_parcela,
		a.vl_parcela,
		b.nr_autorizacao,
		b.ds_comprovante,
		substr(obter_qt_parcela_cartao(a.nr_sequencia),1,10) ds_parcela,
		substr(obter_se_parcela_cartao_concil(null,a.nr_sequencia),1,1) ie_concil,
		a.nr_seq_extrato_parcela
	from    movto_cartao_cr b,
		movto_cartao_cr_parcela a
	where   a.nr_seq_movto    = b.nr_sequencia
	and     coalesce(b.dt_cancelamento::text, '') = ''
	and     b.nr_seq_bandeira = nr_seq_bandeira_p
	and     b.cd_estabelecimento = cd_estabelecimento_p
	and     trunc(b.dt_transacao,'dd') between trunc(to_date(dt_inicial_p),'dd') and fim_dia(to_date(dt_final_p))
	and (trunc(a.dt_parcela,'month') = trunc(to_date(dt_processamento_p),'month') or ie_dt_proc_p = 'N')
	and     a.vl_saldo_liquido > 0) alias20;	
vl_total_w := (vl_nao_conciliado_w + vl_conciliado_w);
vl_nao_conciliado_p 	:= campo_mascara_virgula(vl_nao_conciliado_w);
vl_conciliado_p		:= campo_mascara_virgula(vl_conciliado_w);
vl_total_p		:= campo_mascara_virgula(vl_total_w);	

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
