CREATE OR REPLACE PROCEDURE tranferir_protocolo_disponivel ( nr_protocolo_p text, nr_seq_protocolo_p bigint, dt_mesano_referencia_p timestamp, nr_interno_conta_p bigint) AS $body$
BEGIN
 
if (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') then 
	if (dt_mesano_referencia_p IS NOT NULL AND dt_mesano_referencia_p::text <> '') then 
		update	conta_paciente 
		set	nr_protocolo = nr_protocolo_p, 
			nr_seq_protocolo = nr_seq_protocolo_p, 
			dt_mesano_referencia = to_date(dt_mesano_referencia_p,'dd/mm/yyyy') 
		where	nr_interno_conta = nr_interno_conta_p;
	elsif (nr_protocolo_p IS NOT NULL AND nr_protocolo_p::text <> '') then	 
		update	conta_paciente 
		set	nr_protocolo = nr_protocolo_p, 
			nr_seq_protocolo = nr_seq_protocolo_p 
		where	nr_protocolo = nr_protocolo_p;		
	end if;
end if;
commit;
 
end;
$body$
