CREATE OR REPLACE PROCEDURE transfere_conta_terceiros ( NR_SEQ_CONTA_DESTINO_P bigint, NR_SEQUENCIA_P bigint, NM_USUARIO_P text, NR_ACAO_P bigint, NR_SEQ_CONTA_P bigint) AS $body$
BEGIN

if (NR_ACAO_P = 1) then

update		terceiro_operacao
set  		nr_seq_conta =  	NR_SEQ_CONTA_DESTINO_P,
		nm_usuario = 		NM_USUARIO_P,
		dt_atualizacao = 	clock_timestamp()
where 		nr_sequencia = 		NR_SEQUENCIA_P;

commit;

else

delete
from		terceiro_conta
where		nr_sequencia = 		NR_SEQ_CONTA_P;

commit;

end if;

END;
$body$
