SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE transferir_agenda_pac_html ( nr_seq_origem_p bigint, nr_seq_destino_p bigint, nr_seq_motivo_p bigint, ds_motivo_p text, nr_seq_motivo_prazo_p bigint, ds_motivo_prazo_p text) AS $body$
DECLARE

																

cd_estabelecimento_w			integer;
cd_perfil_w				integer;
nm_usuario_w				varchar(15);
ie_gera_hist_transf_w			varchar(15);
ie_atualiza_prev_autor_w		varchar(15);
cd_setor_atendimento_w			integer;
cd_pessoa_fisica_w			varchar(10);
ie_opme_integracao_origem_w		varchar(15);
ie_status_agenda_origem_w		varchar(15);
hr_inicio_origem_w			timestamp;
nr_minuto_duracao_w			agenda_paciente.nr_minuto_duracao%type;
ie_transf_calculo_w			varchar(1);


BEGIN
cd_estabelecimento_w		:= wheb_usuario_pck.get_cd_estabelecimento;
cd_perfil_w			:= wheb_usuario_pck.get_cd_perfil;
nm_usuario_w			:= wheb_usuario_pck.get_nm_usuario;
cd_setor_atendimento_w		:= wheb_usuario_pck.get_cd_setor_atendimento;
cd_pessoa_fisica_w		:= obter_pf_usuario(wheb_usuario_pck.get_nm_usuario,'C');

select 	max(ie_opme_integracao),
	max(ie_status_agenda),
	max(hr_inicio),
	max(nr_minuto_duracao)
into STRICT	ie_opme_integracao_origem_w,
	ie_status_agenda_origem_w,
	hr_inicio_origem_w,
	nr_minuto_duracao_w
from 	agenda_paciente
where 	nr_sequencia = nr_seq_origem_p;

select    max(coalesce(ie_transf_recalculo,'N'))
into STRICT	  ie_transf_calculo_w
from      parametro_agenda
where     cd_estabelecimento = cd_estabelecimento_w;


ie_atualiza_prev_autor_w := Obter_Param_Usuario(871, 160, cd_perfil_w, nm_usuario_w, cd_estabelecimento_w, ie_atualiza_prev_autor_w);


CALL Transferir_Agenda_Pac(nr_seq_origem_p,nr_seq_destino_p,nm_usuario_w,nr_seq_motivo_p,ds_motivo_p,nr_seq_motivo_prazo_p,ds_motivo_prazo_p);

if (ie_opme_integracao_origem_w = 'S') then
	if (ie_status_agenda_origem_w = 'C') then
		CALL verifica_envio_comunic_opme('E',cd_estabelecimento_w,nr_seq_origem_p,nm_usuario_w,null);
	else	
		CALL verifica_envio_comunic_opme('B',cd_estabelecimento_w,nr_seq_origem_p,nm_usuario_w,null);
	end if;	
end if;

if (ie_atualiza_prev_autor_w = 'S') then
	CALL Atualizar_Prev_Autor_Material(nr_seq_origem_p,nm_usuario_w);
end if;

CALL gerar_comunicacao_transf(nr_seq_origem_p,hr_inicio_origem_w,nm_usuario_w,cd_estabelecimento_w);

if (coalesce(ie_transf_calculo_w,'N') = 'S') then
    CALL update_surgical_schedule(nr_seq_origem_p, nr_minuto_duracao_w, nm_usuario_w);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
