CREATE OR REPLACE PROCEDURE transferir_bordero_tit_js ( nr_bordero_p bigint, nr_titulo_p bigint, nr_bordero_atual_p bigint) AS $body$
DECLARE


dt_liberacao_bordero_w bordero_pagamento.dt_liberacao%type;


BEGIN

select dt_liberacao 
  into STRICT dt_liberacao_bordero_w
  from bordero_pagamento 
  where nr_bordero = nr_bordero_p;

if (dt_liberacao_bordero_w IS NOT NULL AND dt_liberacao_bordero_w::text <> '') then
  CALL wheb_mensagem_pck.exibir_mensagem_abort(1107471);
end if;

update  bordero_tit_pagar
set     	nr_bordero = nr_bordero_p 
where   	nr_titulo  = nr_titulo_p 
	and     nr_bordero = nr_bordero_atual_p;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
