CREATE OR REPLACE PROCEDURE transferir_cirurgia_unidade (nr_unidade_dest_p bigint, nr_atendimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
ie_transfere_cirurgia_w		varchar(01);
dt_entrada_unidade_w		timestamp;
nr_atend_destino_w		bigint;
nr_cirurgia_w			bigint;
qt_reg_proc_w			bigint;
qt_reg_mat_w			bigint;

C01 CURSOR FOR 
	SELECT	distinct nr_cirurgia 
	from	w_transf_cirurgia_atend 
	where	nr_atend_origem = nr_atendimento_p 
	and 	nr_atend_destino = nr_atend_destino_w 
	and 	ie_transfere_cirurgia_w = 'S' 
	and 	nm_usuario	 = nm_usuario_p 
	order by nr_cirurgia;


BEGIN 
 
 
select	coalesce(max(ie_transfere_unidade_cir),'N') 
into STRICT	ie_transfere_cirurgia_w 
from	parametro_faturamento 
where	cd_estabelecimento	= cd_estabelecimento_p;
 
select	dt_entrada_unidade, 
	nr_atendimento 
into STRICT	dt_entrada_unidade_w, 
	nr_atend_destino_w 
from	atend_paciente_unidade 
where	nr_seq_interno = nr_unidade_dest_p;
 
if (ie_transfere_cirurgia_w = 'S') and (dt_entrada_unidade_w IS NOT NULL AND dt_entrada_unidade_w::text <> '') then 
	 
	open C01;
	loop 
	fetch C01 into	 
		nr_cirurgia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		 
		select 	count(*) 
		into STRICT	qt_reg_proc_w 
		from 	procedimento_paciente 
		where 	nr_atendimento = nr_atendimento_p 
		and 	nr_cirurgia = nr_cirurgia_w;
		 
		select 	count(*) 
		into STRICT	qt_reg_mat_w 
		from 	material_atend_paciente 
		where 	nr_atendimento = nr_atendimento_p 
		and 	nr_cirurgia = nr_cirurgia_w;
		 
		if	((qt_reg_mat_w + qt_reg_proc_w) = 0) then 
		 
			update	cirurgia 
			set	dt_entrada_unidade	= dt_entrada_unidade_w, 
				nr_atendimento		= nr_atend_destino_w 
			where	nr_atendimento		= nr_atendimento_p 
			and 	nr_cirurgia		= nr_cirurgia_w;
			 
		end if;
		 
		end;
	end loop;
	close C01;
	 
	delete from w_transf_cirurgia_atend 
	where	nr_atend_origem = nr_atendimento_p 
	and 	nr_atend_destino = nr_atend_destino_w 
	and 	nm_usuario	 = nm_usuario_p;
	 
	commit;
	 
	update	cirurgia 
	set	dt_entrada_unidade	= dt_entrada_unidade_w 
	where	nr_atendimento		= nr_atendimento_p;	
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
