SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE transferir_conta_auditoria ( nr_sequencia_p bigint, ie_tipo_item_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_convenio_parametro_w		integer;
ie_guia_transf_conta_w		varchar(10);
ie_guia_transf_conta_ww		varchar(1);
cd_estabelecimento_w		bigint;
nr_doc_convenio_w		varchar(20);
nr_interno_conta_w		bigint;

			 
 

BEGIN 
 
if (ie_tipo_item_p = 1) then 
 
	select	max(a.cd_convenio_parametro), 
		max(a.cd_estabelecimento) 
	into STRICT	cd_convenio_parametro_w, 
		cd_estabelecimento_w 
	from	conta_paciente a, 
		procedimento_paciente b 
	where	a.nr_interno_conta 	= b.nr_interno_conta 
	and	b.nr_sequencia		= nr_sequencia_p;
	 
	select	coalesce(max(ie_guia_transf_conta),'N') 
	into STRICT	ie_guia_transf_conta_w 
	from	convenio_estabelecimento 
	where	cd_convenio		= cd_convenio_parametro_w 
	and	cd_estabelecimento	= cd_estabelecimento_w;
	 
	select	coalesce(max(ie_guia_transf_conta),'R') 
	into STRICT	ie_guia_transf_conta_ww 
	from	parametro_faturamento 
	where	cd_estabelecimento = cd_estabelecimento_w;
	 
	if (ie_guia_transf_conta_ww = 'R') then 
		 
		update	procedimento_paciente 
		set	nr_doc_convenio	= CASE WHEN ie_guia_transf_conta_w='S' THEN null  ELSE nr_doc_convenio END  
		where	nr_sequencia = nr_sequencia_p;
	 
	elsif (ie_guia_transf_conta_ww = 'T') then 
		 
		update	procedimento_paciente 
		set	nr_doc_convenio	 = NULL 
		where	nr_sequencia = nr_sequencia_p;
	end if;
	 
	if	((ie_guia_transf_conta_w	= 'S' AND ie_guia_transf_conta_ww = 'R') or (ie_guia_transf_conta_ww = 'T')) then 
		CALL atualiza_preco_procedimento(nr_sequencia_p,cd_convenio_parametro_w,nm_usuario_p);
	end if;
	 
else 
 
	select	max(a.cd_convenio_parametro), 
		max(a.cd_estabelecimento) 
	into STRICT	cd_convenio_parametro_w, 
		cd_estabelecimento_w 
	from	conta_paciente a, 
		material_atend_paciente b 
	where	a.nr_interno_conta 	= b.nr_interno_conta 
	and	b.nr_sequencia		= nr_sequencia_p;
	 
	select	coalesce(max(ie_guia_transf_conta),'N') 
	into STRICT	ie_guia_transf_conta_w 
	from	convenio_estabelecimento 
	where	cd_convenio		= cd_convenio_parametro_w 
	and	cd_estabelecimento	= cd_estabelecimento_w;
	 
	select	coalesce(max(ie_guia_transf_conta),'R') 
	into STRICT	ie_guia_transf_conta_ww 
	from	parametro_faturamento 
	where	cd_estabelecimento = cd_estabelecimento_w;
	 
	if (ie_guia_transf_conta_ww = 'R') then 
		 
		update	material_atend_paciente 
		set	nr_doc_convenio	= CASE WHEN ie_guia_transf_conta_w='S' THEN null  ELSE nr_doc_convenio END  
		where	nr_sequencia	= nr_sequencia_p;
	 
	elsif (ie_guia_transf_conta_ww = 'T') then 
	 
		update	material_atend_paciente 
		set	nr_doc_convenio	 = NULL 
		where	nr_sequencia	= nr_sequencia_p;
	end if;
	 
	select	nr_doc_convenio, 
		nr_interno_conta 
	into STRICT	nr_doc_convenio_w, 
		nr_interno_conta_w 
	from	material_atend_paciente 
	where	nr_sequencia	= nr_sequencia_p;
	 
	if	((ie_guia_transf_conta_w	= 'S' AND ie_guia_transf_conta_ww = 'R') or (ie_guia_transf_conta_ww = 'T')) then 
		CALL atualiza_preco_material(nr_sequencia_p,nm_usuario_p);
	end if;
	 
	select	nr_doc_convenio, 
		nr_interno_conta 
	into STRICT	nr_doc_convenio_w, 
		nr_interno_conta_w 
	from	material_atend_paciente 
	where	nr_sequencia	= nr_sequencia_p;
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE transferir_conta_auditoria ( nr_sequencia_p bigint, ie_tipo_item_p bigint, nm_usuario_p text) FROM PUBLIC;

